import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { submitAnswer, finishQuiz } from '../mockServer';

export default function Question() {
  const location = useLocation();
  const navigate = useNavigate();
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);

  const { quizId, questions } = location.state;

  const handleAnswerSubmit = () => {
    const question = questions[currentQuestionIndex];
    const timeTaken = 10; // replace with actual time logic
    submitAnswer(quizId, question.id, selectedOption, timeTaken); // Simulating API call

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedOption(null);
    } else {
      const result = finishQuiz(); // Simulating API call
      Navigate.push({
        pathname: '/report',
        state: result,
      });
    }
  };

  return (
    <div className="question">
      <h2>{questions[currentQuestionIndex].question}</h2>
      {questions[currentQuestionIndex].options.map((option, index) => (
        <label key={index}>
          <input
            type="radio"
            value={index}
            checked={selectedOption === index}
            onChange={() => setSelectedOption(index)}
          />
          {option}
        </label>
      ))}
      <button disabled={selectedOption === null} onClick={handleAnswerSubmit}>
        Next
      </button>
    </div>
  );
}
