import React, { useState } from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import './Question.css';

const Question = () => {
  const [progress, setProgress] = useState(40);  // 40% as an example for now
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  const handleNext = () => {
    console.log('Answer Submitted:', selectedOption);
    // You can update the progress or navigate to the next question
  };

  return (
    <div className="question-container">
      <header className="header">
        <img
          src="https://your-logo-url.com/logo.png"
          alt="Upraised Logo"
          className="logo"
        />
      </header>
      <div className="progress-container">
        <CircularProgressbar
          value={progress}
          text={`${progress}%`}
          styles={buildStyles({
            textSize: '16px',
            pathColor: `#ff4e4e`,
            textColor: '#ff4e4e',
          })}
        />
      </div>
      <div className="question-box">
        <h2>What is the capital of France?</h2>
        <div className="options">
          <label>
            <input
              type="radio"
              name="option"
              value="Paris"
              onChange={() => handleOptionSelect('Paris')}
              checked={selectedOption === 'Paris'}
            />
            Paris
          </label>
          <label>
            <input
              type="radio"
              name="option"
              value="London"
              onChange={() => handleOptionSelect('London')}
              checked={selectedOption === 'London'}
            />
            London
          </label>
          <label>
            <input
              type="radio"
              name="option"
              value="Berlin"
              onChange={() => handleOptionSelect('Berlin')}
              checked={selectedOption === 'Berlin'}
            />
            Berlin
          </label>
        </div>
      </div>
      <button className="next-button" onClick={handleNext}>
        Next
      </button>
    </div>
  );
};

export default Question;
