import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { submitAnswer, finishQuiz } from '../mockServer';

function Question() {
  const location = useLocation();
  const navigate = useNavigate();
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);

  const { quizId = null} = location.state || {};

  const handleAnswerSubmit = () => {
    const question = questions[currentQuestionIndex];
    const timeTaken = 10; // replace with actual time logic
    submitAnswer(quizId, question.id, selectedOption, timeTaken); // Simulating API call

    useEffect(() => {
      if (!quizId) {
        // Redirect to home if no quizId is found
        navigate('/');
      }
    }, [quizId, navigate]);
  
    const submitAnswer = () => {
      if (selectedOption) {
        // Handle answer submission and move to the next question or report
        navigate('/report');
      }
  };

  return (
    <div>
      <h1>Question 1</h1>
      <div>
        <input type="radio" id="option1" name="option" value="Option 1" onChange={() => setSelectedOption('Option 1')} />
        <label htmlFor="option1">Option 1</label>
      </div>
      <button onClick={submitAnswer}>Submit</button>
    </div>
  );
}}


export default Question;
