import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Question = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Safely destructure quizId from location.state or set to null if not available
  const { quizId } = location.state || {};

  const [selectedOption, setSelectedOption] = useState(null);
  const [question, setQuestion] = useState(null);

  useEffect(() => {
    if (!quizId) {
      // Redirect to home if no quizId is found
      navigate('/');
    } else {
      // Fetch the question data (this is a placeholder; replace with actual API call)
      setQuestion({
        id: 1,
        text: 'What is the capital of France?',
        options: ['Paris', 'London', 'Rome', 'Berlin'],
      });
    }
  }, [quizId, navigate]);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  const handleSubmit = () => {
    if (selectedOption) {
      // Submit the selected option and move to the next question or report
      console.log('Submitting answer:', selectedOption);
      navigate('/report', { state: { quizId, score: 10 } }); // Example score
    }
  };

  return (
    <div>
      {question ? (
        <>
          <h1>Question {question.id}</h1>
          <p>{question.text}</p>
          <div>
            {question.options.map((option, index) => (
              <div key={index}>
                <input
                  type="radio"
                  id={`option${index}`}
                  name="option"
                  value={option}
                  onChange={() => handleOptionChange(option)}
                />
                <label htmlFor={`option${index}`}>{option}</label>
              </div>
            ))}
          </div>
          <button onClick={handleSubmit}>Submit</button>
        </>
      ) : (
        <p>Loading question...</p>
      )}
    </div>
  );
};

export default Question;
