import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Question = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Safely get quizId from location.state or default to undefined
  const quizId = location.state?.quizId;

  const [selectedOption, setSelectedOption] = useState(null);
  const [question, setQuestion] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!quizId) {
      // Redirect to home if no quizId is found
      navigate('/');
      return;
    }

    // Fetch the question data from the mock server
    axios
      .get(`http://localhost:5000/api/questions/${quizId}`)
      .then((response) => {
        setQuestion(response.data);
        setLoading(false);
      })
      .catch((error) => {
        setError('Failed to load question');
        setLoading(false);
      });
  }, [quizId, navigate]);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
  };

  const handleSubmit = () => {
    if (selectedOption) {
      // Submit the selected option and move to the next question or report
      console.log('Submitting answer:', selectedOption);
      navigate('/report', { state: { quizId, score: 10 } }); // Example score
    }
  };

  if (loading) {
    return <p>Loading question...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      {question ? (
        <>
          <h1>Question {question.id}</h1>
          <p>{question.text}</p>
          <div>
            {question.options.map((option, index) => (
              <div key={index}>
                <input
                  type="radio"
                  id={`option${index}`}
                  name="option"
                  value={option}
                  onChange={() => handleOptionChange(option)}
                />
                <label htmlFor={`option${index}`}>{option}</label>
              </div>
            ))}
          </div>
          <button onClick={handleSubmit}>Submit</button>
        </>
      ) : (
        <p>No question available.</p>
      )}
    </div>
  );
};

export default Question;
