import React, { useState, useEffect } from 'react';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import { fetchQuestions } from './api';
import './Question.css';

const Question = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const getQuestions = async () => {
      const quizQuestions = await fetchQuestions();
      setQuestions(quizQuestions);
    };

    getQuestions();
  }, []);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  const handleNext = () => {
    if (selectedOption) {
      setSelectedOption(null);  // Reset the selected option
      const nextIndex = currentQuestionIndex + 1;
      if (nextIndex < questions.length) {
        setCurrentQuestionIndex(nextIndex);
        setProgress(((nextIndex + 1) / questions.length) * 100);  // Update progress
      } else {
        // Handle quiz completion here, such as showing a results screen
        console.log('Quiz complete!');
      }
    }
  };

  if (questions.length === 0) {
    return <div>Loading...</div>;  // Show loading message until questions are fetched
  }

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="question-container">
      <header className="header">
        <img
          src="https://your-logo-url.com/logo.png"
          alt="Upraised Logo"
          className="logo"
        />
      </header>
      <div className="progress-container">
        <CircularProgressbar
          value={progress}
          text={`${Math.round(progress)}%`}
          styles={buildStyles({
            textSize: '16px',
            pathColor: `#ff4e4e`,
            textColor: '#ff4e4e',
          })}
        />
      </div>
      <div className="question-box">
        <h2 dangerouslySetInnerHTML={{ __html: currentQuestion.question }} />
        <div className="options">
          {currentQuestion.incorrect_answers.concat(currentQuestion.correct_answer).map((option, index) => (
            <label key={index}>
              <input
                type="radio"
                name="option"
                value={option}
                onChange={() => handleOptionSelect(option)}
                checked={selectedOption === option}
              />
              <span dangerouslySetInnerHTML={{ __html: option }} />
            </label>
          ))}
        </div>
      </div>
      <button
        className="next-button"
        onClick={handleNext}
        disabled={!selectedOption}  // Disable button if no option is selected
      >
        Next
      </button>
    </div>
  );
};

export default Question;
