import React, { useState } from 'react';

function QuizComponent() {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [showNextButton, setShowNextButton] = useState(false);

  const questions = [
    {
      prompt: "How do you judge what should be added in the next version of the app?",
      options: ["Data Analysis", "User's feedback", "Copy from similar product", "Make a questionary", "Personal feeling"],
    },
    {
      prompt: "How do you judge what should be added in the next version of the app?",
      options: ["Data Analysis", "User's feedback", "Copy from similar product", "Make a questionary", "Personal feeling"],
    },
    {
      prompt: "How do you judge what should be added in the next version of the app?",
      options: ["Data Analysis", "User's feedback", "Copy from similar product", "Make a questionary", "Personal feeling"],
    },
    // Add more questions here
  ];

  const handleAnswerSelection = (answer) => {
    setSelectedAnswer(answer);
    setShowNextButton(true);
  };

  const handleNextQuestion = () => {
    if (selectedAnswer === null) {
      // Handle case where no answer is selected
      alert("Please select an answer before proceeding.");
    } else {
      setSelectedAnswer(null);
      setShowNextButton(false);
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-container">
      <div className="progress-bar">
        <div className="progress-bar-inner" style={{ width: `${(currentQuestionIndex + 1) / questions.length * 100}%` }}></div>
      </div>
      <h2>Quiz Question {currentQuestionIndex + 1}</h2>
      <p>{currentQuestion.prompt}</p>
      <ul>
        {currentQuestion.options.map((option, index) => (
          <li key={index}>
            <input type="radio" name="answer" value={option} onChange={() => handleAnswerSelection(option)} />
            <label htmlFor={option}>{option}</label>
          </li>
        ))}
      </ul>
      {showNextButton && <button onClick={handleNextQuestion}>Next</button>}
    </div>
  );
}

export default QuizComponent;
